import React, { useState, useEffect } from 'react';
import '../../App.css';
import firebase from 'firebase/app';
import 'firebase/firestore';

function Debates() {
  const [debates, setDebates] = useState([]);

  // Function to fetch debates from Firestore
  const fetchDebates = async () => {
    try {
      const debatesCollection = firebase.firestore().collection('debates');
      const snapshot = await debatesCollection.get();
      const debatesData = snapshot.docs.map(doc => doc.data());
      setDebates(debatesData);
    } catch (error) {
      console.error('Error fetching debates:', error);
    }
  };

  useEffect(() => {
    // Load debates when component mounts
    fetchDebates();
  }, []);

  return (
    <>
      <button /*onClick={}*/>Create Debate</button>

      <div className="debates-container">
        {/* Map through debates and display as item cards */}
        {debates.map((debate, index) => (
          <div key={index} className="debate-card">
            <h3>{debate.title}</h3>
            <p>{debate.description}</p>
            {/* Additional debate info can go here */}
          </div>
        ))}
      </div>
    </>
  );
}

export default Debates;
